public class nested_if_else_stat {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int year=2018;
		int ielts_score=6;
		if(year>=2019) {
			if(ielts_score>=6) {
				System.out.println("you are eligible for TR-PR pathway");
			}
		}
			else {
				System.out.println("you are not eligible for TR-PR pathway");
			}
		}
		
	}
		



